import React, {useEffect,useState} from 'react'
import {createDeckAndDraw, drawCardFromDeck} from './api'
import {CardLayout} from './Layout.component'
import { ButtonsTab } from './ButtonsTab'
import { compareValues } from './utils'





export const GameBoard = () => {
    const [cardState, setCardState] = useState({
        cardValue:null,
        cardImageUrl:null
    })
    const [deckId, setDeckId]=useState(null)
    const [result,setResult]=useState(null)

    useEffect( async ()=>{
        const{deckId, value, image} = await createDeckAndDraw();
        setCardImageUrl(image);
        setCardValue(value);
        setDeckId(deckId);
    },[])

    const onButtonClick = async (event) =>{
        const bet=event.target.name
        const {oldDeckId,newValue,newImage} = await drawCardFromDeck(deckId);

        const result = compareValues({cardValue,newValue,bet})
        debugger
        console.log("Button was clicked")
        setResult(result)
        setCardValue(newValue)
        setCardImageUrl(newImage)
    }

    if(!cardImageUrl){
        return <h1>loading</h1>
    }

    return (
        <CardLayout>
            <img src={cardImageUrl}></img>
            <ButtonsTab onButtonClick={onButtonClick}/>
            {result &&<h2>{result}</h2>}
        </CardLayout>
    )
}
