import React, {useEffect,useState} from 'react'

export const useBoardGame = () => {

    //states
    const [{cardImageUrl,cardValue}, setCardState] = useState({
        cardValue:null,
        cardImageUrl:null
    })
    const [deckId, setDeckId]=useState(null)
    const [result,setResult]=useState(null)

    //use effect sychonrous to prevent race conditions
    useEffect(()=>{
        const fetchCardandDeck = async () =>{
            const {deckId, value, image} = await createDeckAndDraw();
            setCardState({
                cardValue:value,
                cardImageUrl:image
            })
            setDeckId(deckId);
        }
        fetchCardandDeck();
    },[])

    const onButtonClick = async (event) =>{
        const bet=event.target.name
        const {value,image} = await drawCardFromDeck(deckId);


        const result = compareValues({
            previousCardValue: cardValue,
            currentCardValue: value,
            bet
        })

        setCardState({
            cardImageUrl:image,
            cardValue:value
        })

        setResult(result)
    }
    return {
        cardImageUrl,
        onButtonClick,
        result
    }
}
