import React, {useEffect,useState} from 'react'
import { compareValues } from './utils'
import {createDeckAndDraw, drawCardFromDeck} from './api'

export const useBoardGame = () => {

    //states
    const [{cardImageUrl,cardInHand}, setCardState] = useState({
        cardInHand:null,
        cardImageUrl:null
    })
    const [deckId, setDeckId]=useState(null)
    const [result,setResult]=useState(null)
    const [score,setScore]=useState(null)

    //turn
    const [turn,setTurn]=useState(true)

    //use effect sychonrous to prevent race conditions
    useEffect(()=>{
        const fetchCardandDeck = async () =>{
            const {deckId,cardsArray} = await createDeckAndDraw();
            console.log({deckId,cardsArray})

            //convert object to array of values
             const cards = cardsArray.map(obj => {
                 return obj.value
             })

             //convert object to array of images
             const images = cardsArray.map(obj => {
                return obj.image
            })

            setCardState({
                cardInHand:cards,
                cardImageUrl:images
            })
            setDeckId(deckId);
        }
        fetchCardandDeck();
    },[])

    useEffect(()=>{
            const fetchNewCard = async () =>{
                const {deckId,value,image} = await drawCardFromDeck(deckId);
                console.log({deckId,value,image})
            }
            fetchNewCard();


    },[turn])

    // const onButtonClick = async (event) =>{
    //     const bet=event.target.name
    //     const {value,image} = await drawCardFromDeck(deckId);


    //     const result = compareValues({
    //         previousCardValue: cardValue,
    //         currentCardValue: value,
    //         bet
    //     })

    //     setCardState({
    //         cardImageUrl:image,
    //         cardValue:value
    //     })

    //     setResult(result)
    // }

    const onCardClick = (event) => {
        let srcImgCard = event.target.src
        let pos = cardImageUrl.indexOf(srcImgCard)
        let selectedCardValue = cardInHand[pos]

        let updatedScore = Number(score)+Number(selectedCardValue)

        if(turn)
        {
            //draw another card

                //update score
            setScore(updatedScore)

            //remove card from objects
            setCardState({
                cardInHand:cardInHand.filter(item => item!=selectedCardValue),
                cardImageUrl:cardImageUrl.filter(item => item!=srcImgCard)
            })

            //change turn
            setTurn(!turn)
        }

    }


    return {
        score,
        cardImageUrl,
        //onButtonClick,
        onCardClick,
        result
    }
}
