import axios from 'axios'

const api = axios.create({
    baseURL:'https://deckofcardsapi.com/api/deck/'
})

//draw 5 cards for initial hand
export const createDeckAndDraw = async () =>{
    const {data} = await api.get('new/shuffle',{
        params:{
            deck_count: 1
        }
    })
    console.log({data})
    //rename
    const {deck_id:deckId} = data;

    const {data:cardResponse} = await api.get(`${deckId}/draw/`,{
        params:{
            count:5
        }
    })
    console.log(cardResponse)
    console.log(cardResponse.suit)
    // const cardSuits = cardResponse.map(cards=> {
    //     let arr = [cards.value,cards.suit]
    //     console.log(arr)
    // }
    // )
    //return object
    return {cardsArray:cardResponse.cards,deckId:deckId}
}

export const drawFromDeck_2 = async (deckId) => {
    const {data:cardResponse} = await api.get(`${deckId}/draw/`,{
        params:{
            count:5
        }
    })
    //return object
    return {cardsArray:cardResponse.cards}
}



export const drawCardFromDeck = async (deckId) => {
    const {data} = await api.get(`${deckId}/draw/`,{
        params:{
            count:1
        }
    })

    const {cards} = data
    const {value,image} = cards[0]
    return {deckId,value,image}
}
