import React, { useEffect, useState } from "react";

import { CardLayout, ScoreLayout } from "./Layout.component";
import { ButtonsTab } from "./ButtonsTab";

import { useBoardGame } from "./cardGameBoard.state";

export const GameBoard = () => {
  const { cardImageUrl, onButtonClick, result, score, onCardClick,cardImageUrl_2,turn,winner} = useBoardGame();

  if (!cardImageUrl) {
    return <h1>loading</h1>;
  }

  return (
    <div>
      <ScoreLayout>
        {score}
        {turn?
            winner===100 ?<h1>player 1</h1> : <h1>player 1 winner</h1>
            :
            <h1>player 2</h1>
        }
      </ScoreLayout>
      <CardLayout>
        {
        turn ?
        cardImageUrl.map((eachCardUrl) => {
          return <img src={eachCardUrl} onClick={onCardClick}></img>;
        })
        :
        cardImageUrl_2.map((eachCardUrl) => {
          return <img src={eachCardUrl} onClick={onCardClick}></img>;
        })

        }
      </CardLayout>
      <div>
        <ButtonsTab onButtonClick={onButtonClick} />
        {result && <h2>{result}</h2>}
      </div>

    </div>
  );
};
