const cardValues = [
    '2',
    '3',
    '4',
    '5',
    '6',
    '7',
    '8',
    '9',
    '10',
    'ACE',
    'JACK',
    'QUEEN',
    'KING'
];

export const compareValues = ({cardValue,newValue,bet}) =>{
    const prevCardValueIndex = cardValues.indexOf(cardValue)
    const currentCardValueIndex = cardValues.indexOf(newValue)

    if(prevCardValueIndex === -1 || currentCardValueIndex==1){
        throw new Error('Not found')
    }

    if(bet ==="up" && prevCardValueIndex >= currentCardValueIndex){
        return "lose"
    }
    if(bet ==="up" && prevCardValueIndex < currentCardValueIndex){
        return "win"
    }

    if(bet ==="down" && prevCardValueIndex <= currentCardValueIndex){
        return "lose"
    }
    if(bet ==="down" && prevCardValueIndex > currentCardValueIndex){
        return "win"
    }
}
