import React, {useEffect,useState} from 'react'
import { cardsObjToArray, imagesObjToArray } from './utils'
import {createDeckAndDraw, drawCardFromDeck,drawFromDeck_2} from './api'

export const useBoardGame = () => {

    //states
    //player1
    const [{cardImageUrl,cardInHand}, setCardState] = useState({
        cardInHand:null,
        cardImageUrl:null
    })
    // //player2
    // const [{cardImageUrl_2,cardInHand_2}, setCardState_2] = useState({
    //     cardInHand_2:null,
    //     cardImageUrl_2:null
    // })

    const [deckId, setDeckId]=useState(null)
    const [score,setScore]=useState(null)

    //turn
    const [turn,setTurn]=useState(true)
    const [winner,setWinner]=useState()

    //handle ace 
    const [ace,setAce]=useState(null)

    //use effect sychonrous to prevent race conditions
    useEffect(()=>{
        const fetchCardandDeck = async () =>{

            //draw 5 cards
            const {deckId,cardsArray} = await createDeckAndDraw();

            //draw 5 cards for AI
             const {cardsArray:AIcardsArray} = await drawFromDeck_2(deckId);

               //convert object to array of values
             const cards =  cardsObjToArray(cardsArray)
             const images = imagesObjToArray(cardsArray)

             //AI convert object to array of values
             const AIcards =  cardsObjToArray(AIcardsArray)

            setCardState({
                cardInHand:cards,
                cardImageUrl:images
            })
            // setCardState_2({
            //     cardInHand_2:cards2,
            //     cardImageUrl_2:images2
            // })

            setDeckId(deckId);
        }
        fetchCardandDeck();
    },[])

    useEffect(()=>{

            const fetchNewCard = async () =>{
                const {value,image} = await drawCardFromDeck(deckId);
                setCardState({
                    cardInHand:[...cardInHand,value],
                    cardImageUrl:[...cardImageUrl,image]
                })
            }
            // const fetchNewCard2 = async () =>{
            //     const {value,image} = await drawCardFromDeck(deckId);
            //     setCardState_2({
            //         cardInHand_2:[...cardInHand,value],
            //         cardImageUrl_2:[...cardImageUrl,image]
            //     })

            // }
            if(turn){
                fetchNewCard();
            }
            // else{
            //     fetchNewCard2();
            // }

    },[turn])

    const handleAce = () =>{
        const aceValue = prompt("Please enter your ace value from 1-11", "1");
        return aceValue;

    }

    const onCardClick = (event) => {
        if(turn)
        {
            let srcImgCard = event.target.src
            let pos = cardImageUrl.indexOf(srcImgCard)
            let selectedCardValue = cardInHand[pos]

            if(selectedCardValue=='4'){
                setCardState({
                    cardInHand:cardInHand.filter(item => item!=selectedCardValue),
                    cardImageUrl:cardImageUrl.filter(item => item!=srcImgCard)
                })
                //setTurn(!turn)
                return
            }
            else if(selectedCardValue=='9'){
                setCardState({
                    cardInHand:cardInHand.filter(item => item!=selectedCardValue),
                    cardImageUrl:cardImageUrl.filter(item => item!=srcImgCard)
                })
               // setTurn(!turn)
                return
            }
            else if(selectedCardValue=='10'){
                selectedCardValue=-10
            }
            else if(selectedCardValue=='KING'){
                 selectedCardValue=99-Number(score)
            }
            else if(selectedCardValue=='QUEEN'){
                selectedCardValue=12
            }
            else if(selectedCardValue=='JACK'){
                selectedCardValue=11
            }
            else if(selectedCardValue=='ACE'){

                selectedCardValue=handleAce();
            }

            let updatedScore = Number(score)+Number(selectedCardValue)

            if (updatedScore > 99){
               alert("Winner Player 1 Refresh Game")
            }

                //update score
            setScore(updatedScore)

            //remove card from objects
            setCardState({
                cardInHand:cardInHand.filter(item => item!=selectedCardValue),
                cardImageUrl:cardImageUrl.filter(item => item!=srcImgCard)
            })

            //change turn
            //setTurn(!turn)
        }
        //player 2 turn
        // else{
        //     let srcImgCard = event.target.src
        //     let pos = cardImageUrl_2.indexOf(srcImgCard)
        //     let selectedCardValue = cardInHand_2[pos]
        //     if(selectedCardValue=='4'){
        //         setCardState_2({
        //             cardInHand:cardInHand_2.filter(item => item!=selectedCardValue),
        //             cardImageUrl:cardImageUrl_2.filter(item => item!=srcImgCard)
        //         })
        //         setTurn(!turn)
        //         return
        //     }
        //     else if(selectedCardValue=='9'){
        //         setCardState_2({
        //             cardInHand:cardInHand_2.filter(item => item!=selectedCardValue),
        //             cardImageUrl:cardImageUrl_2.filter(item => item!=srcImgCard)
        //         })
        //         setTurn(!turn)
        //         return
        //     }
        //     else if(selectedCardValue=='10'){
        //         selectedCardValue=-10
        //     }
        //     else if(selectedCardValue=='KING'){
        //          selectedCardValue=99-Number(score)
        //     }
        //     else if(selectedCardValue=='QUEEN'){
        //         selectedCardValue=12
        //     }
        //     else if(selectedCardValue=='JACK'){
        //         selectedCardValue=11
        //     }
        //     else if(selectedCardValue=='ACE'){  

        //         selectedCardValue=handleAce();
        //     }


        //     let updatedScore = Number(score)+Number(selectedCardValue)
        //     debugger
        //     if (updatedScore > 99){
        //         alert("Winner player 2! Refresh Game")
        //     }

        //     setScore(updatedScore)

        //     //remove card from objects
        //     setCardState_2({
        //         cardInHand_2:cardInHand_2.filter(item => item!=selectedCardValue),
        //         cardImageUrl_2:cardImageUrl_2.filter(item => item!=srcImgCard)
        //     })

        //     //change turn
        //     setTurn(!turn)
        // }

    }


    return {
        score,
        cardImageUrl,
        //cardImageUrl_2,
        //onButtonClick,
        onCardClick,
        turn,
        winner,
    }
}
