import React, {useEffect,useState} from 'react'

import {CardLayout} from './Layout.component'
import { ButtonsTab } from './ButtonsTab'

import { useBoardGame } from './cardGameBoard.state'






export const GameBoard = () => {

    const {cardImageUrl,onButtonClick,result} = useBoardGame();

    if(!cardImageUrl){
        return <h1>loading</h1>
    }

    return (
        <div>
        <CardLayout>
        {
            cardImageUrl.map((eachCardUrl)=>{return <img src={eachCardUrl}></img>})
        }          
        </CardLayout>
        <div>
        <ButtonsTab onButtonClick={onButtonClick}/>
         {result &&<h2>{result}</h2>}
        </div>
        </div>

    )
}
